str = ""

def is_palin(s):
    return s == s[::-1]
def longestPalindrome(s):
    longest_str = ''
    for i in range(len(s)):
    	# print(i)
    	for j in range(len(s),i,-1):
            # print(j)
            sub_str = s[i:j]
            # print(sub_str)
            if is_palin(sub_str) and len(longest_str) < len(sub_str):
                longest_str = sub_str
    print(longest_str)
    return longest_str

longestPalindrome(str)
